import {useRef, useState} from 'react';
import './button.scss'

export const  ButtonComponent = () => {
    const [hover, setHover] = useState();
    const amountList = [25, 50, 100, 200, 250]
    let btnList = []
    const ref = useRef(null)

    const handleClick = (element, index) => {
        console.log(element)
        return document.getElementsByClassName('btn-'+ element).focus()
        // debugger
        // return ref.current.focus()
    }

    const mouseIn = () => {
        console.log("IN")
        setHover(true)
    }

    const mouseOut = () => {
        console.log("OUT")
        setHover(false)
    }

    const customAmount = (
        <div>
            <input placeholder="Enter custome amount here ..."/>
        </div>  
    )
    const amountBtn = amountList.forEach( (element, index) => {
        return btnList = [...btnList, <button onMouseOver = {mouseIn} onMouseOut={mouseOut} className={`btn-${element}`} ref={ref} key={`${element}`} onClick={handleClick(element)}>{element}</button>]
    })
    return (
      <div className="button">
        {btnList}
        {customAmount}
      </div>
    );
  }
