import {useRef, useState} from 'react';
import './button.scss'

export const  ButtonComponent = () => {
    const [hover, setHover] = useState();
    const [showCustomeInput, setCustomeAmount] = useState(false)
    const amountList = ['Custom', 50, 100, 200, 250]
    let btnList = []
    const ref = useRef(null)

    const handleClick = (index) => {
        if (index === 0) setCustomeAmount(true); else setCustomeAmount(false)

    }

    const mouseIn = () => {
        console.log("IN")
        setHover(true)
    }

    const mouseOut = () => {
        console.log("OUT")
        setHover(false)
    }

    const customAmount = (
        <div>
            <input placeholder="Enter custome amount here ..."/>
        </div>  
    )
    const amountBtn = amountList.forEach( (element, index) => {
        return btnList = [...btnList, <button type="radio" onMouseOver = {mouseIn} onMouseOut={mouseOut} className={`btn-${element}`}  key={`${element}`} onClick={handleClick(index)}>{element}</button>]
    })
    return (
      <div className="button">
        {btnList}
        {showCustomeInput && customAmount}
      </div>
    );
  }
