import {useRef, useState} from 'react';
import './button.scss'

export const  ButtonComponent = () => {
    const [hover, setHover] = useState();
    const [showCustomeInput, setCustomeInput] = useState()
    const amountList = ['Custom', 50, 100, 200, 250]
    let btnList = []
    const ref = useRef(null)

    const handleClick = (index) => {
        debugger
        if(index === 0){
            setCustomeInput(true);  
        }
        return
    }

    const customAmount = (
        <div>
            <input placeholder="Enter custome amount here ..."/>
        </div>  
    )
    const amountBtn = amountList.forEach( (element, index) => {
        return btnList = [...btnList, <button type="radio"  className={`btn-${element}`}  key={`${element}`} onClick={() => handleClick(index)}>{element}</button>]
    })
    return (
      <div className="button">
        {btnList}
        {showCustomeInput && customAmount}
      </div>
    );
  }
