import {useRef} from 'react';

export const  ButtonComponent = () => {
    const amountList = [25, 50, 100, 200, 250]
    let btnList = []
    const btnRef = useRef(null)

    const handleClick = () => {
        return ref.current.focus()
    }

    const customAmount = (
        <div>
            <input placeholder="Enter custome amount here ..."/>
        </div>  
    )
    const amountBtn = amountList.forEach( (element, index) => {
        return btnList = [...btnList, <button ref={btnRef} key={`${element}`} onClick={handleClick}>{element}</button>]
    })
    return (
      <div className="button">
        {btnList}
        {customAmount}
      </div>
    );
  }
