import {useRef, useState} from 'react';
import './button.scss'

export const  ButtonComponent = () => {
    const [showCustomeInput, setCustomeInput] = useState()
    const amountList = ['Custom', 50, 100, 200, 250]
    let btnList = []

    const handleClick = (index) => {
        index === 0 ? 
            setCustomeInput(true): setCustomeInput(false)

    }

    const customAmount = (
        <div>
            <input placeholder="Enter custome amount here ..."/>
        </div>  
    )

    const tt = amountList.forEach( (element, index) => {
        return btnList = [
            ...btnList, 
            <button 
                type="radio"  
                className={`btn-${element}`}  
                key={`${element}`} 
                onClick={() => handleClick(index)}>
                    {element}
            </button>]
    })

    return (
      <div className="button">
        {btnList}
        {showCustomeInput && customAmount}
      </div>
    );
  }
